    public function savedBlocksJson($file)
    {
        $fileBlock   = file_get_contents(WWW_ROOT . 'master-assets' . DS . 'plugins' . DS . 'froala-blocks' . DS . 'saved'. DS . $file);
        $decodeBlock = json_decode($fileBlock, true);
        $html        = $decodeBlock['html'];

