    public function themeBlocks($theme = 'EngageTheme')
    {
        $blocksFolder = new Folder(PLUGINS . $theme.'/webroot/blocks/');
        $blocks       = $blocksFolder->find('.*\.json', true);
        
        return $blocks;
    } 
    public function themeBlocksJson($file, $theme = 'EngageTheme')
    {
        $fileBlock   = file_get_contents(PLUGINS .'EngageTheme/webroot/blocks/'.$file);
        $decodeBlock = json_decode($fileBlock, true);
        $html        = '';

        foreach($decodeBlock["options"] as $options) {
            $editable = $options['editable'];
            $explodeEditable = explode(',', $editable);
            $newArray = [];
            foreach ($explodeEditable as $oldArray) {
                $newArray[] = '{'. $oldArray .'}';
            }

            foreach ($explodeEditable as $finder) {
                $replaceHtml = str_replace($newArray, '', $options['html']);
            }
            $html .= $replaceHtml;
        }

        return $html;

    }
